import React, { useState, useEffect } from "react";
import './cardInfo.css'
import TextField from "@mui/material/TextField";
import EditNoteIcon from "@mui/icons-material/EditNote";
import CloseIcon from "@mui/icons-material/Close";
import { useNavigate, useParams } from "react-router";
import { setListData,setDescription, setShowDescription } from "../../../store/slices/taskSlices";
import { useDispatch, useSelector } from 'react-redux';
import DashboardIcon from '@mui/icons-material/Dashboard';
import styled from "styled-components"

const Person = styled.span`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 28px;
  width: 28px;
  border-radius: 100%;
  font-size: 10px;
  font-family: "Lato", sans-serif;
  font-weight: 900;
  color: #ffffff;
  background-color: #c335fb;
`;
export default function CardInfo() {
  const [currentCardTitle, setCurrentCardTitle] = useState(""); // Added currentCardTitle
  const [currentTaskTitle, setCurrentTaskTitle] = useState(""); // Added currentTaskTitle
  const [currentTask, setCurrentTask] = useState({});

  const navigate = useNavigate();
  const { Cid, Lid } = useParams();

  const dispatch = useDispatch()
  const listData = useSelector((state) => state.tasks.listData)
  const description = useSelector((state) => state.tasks.description);
  const showDescription = useSelector((state) => state.tasks.showDescription);

  useEffect(() => {
    let input = [...listData];
    let index = input.findIndex((ele) => ele.id === Cid);

    let currentCard = { ...input[index] };
    let taskss = { ...currentCard };
    let Task = [...taskss.task];

    let taskindex = Task.findIndex((ele) => ele.id === Lid);

    let currentTask = Task[taskindex];

    if (currentTask) {
      console.log(currentTask)
      setDescription(currentTask.description);
      setCurrentCardTitle(currentCard.listName); // Set currentCardTitle
      setCurrentTaskTitle(currentTask.title); // Set currentTaskTitle
    }
  }, [showDescription]);

  function handleDescription() {
    let input = [...listData];
    let index = input.findIndex((ele) => ele.id === Cid);
    let currentCard = { ...input[index] };
    let taskss = { ...currentCard };
    let Task = [...taskss.task];
    let taskindex = Task.findIndex((ele) => ele.id === Lid);
    let currentTask = Task[taskindex];
    let particularTask = { ...currentTask };
    particularTask.description = description;
    Task.splice(taskindex, 1, particularTask);
    taskss.task = Task;
    input.splice(index, 1, taskss);
    dispatch(setListData(input));
    localStorage.setItem("List", JSON.stringify(input));
    setShowDescription(false);
  }

  return (
    <div className="Ibox_mainBackground">
      <div className="Ibox_container">
        <section className="Ibox_titleSection">
          <div>
            <div className="Ibox_iconPart">
              <DashboardIcon />
            </div>
            <div className="Ibox_contentPart">
              <h3>{currentTaskTitle}</h3>
              <p>in List {currentCardTitle}</p>
            </div>
          </div>
          <CloseIcon onClick={() => navigate("/")} />
        </section>

        <section className="Ibox_descriptionSection">
          <div className="Ibox_iconPart">
            <EditNoteIcon fontSize="large" />
          </div>
          <div className="Ibox_contentPart">
            <h4>Description</h4>
            <div className="description_box">
              {!showDescription ? (
                <div className="Desc_Start">
                  <p>{currentTask.description}</p>
                  <button onClick={handleDescription}>Edit</button>
                </div>
              ) : (
                <>
                  <TextField
                    multiline
                    sx={{ width: "100%" }}
                    value={description}
                    onChange={(e) => {
                      setDescription(e.target.value);
                    }}
                  />
                  <button
                    className="btn"
                    onClick={handleDescription}
                    style={{ backgroundColor: "blue", color: "white" }}
                  >
                    Save
                  </button>
                  <button
                    className="btn"
                    onClick={() => {
                      setShowDescription(false);
                      setDescription(currentTask.description);
                    }}
                  >
                    Cancel
                  </button>
                </>
              )}
            </div>
          </div>
        </section>
      </div>
    </div>
  );
}
