      color: persons.AM.color
    },
    {
      id: "card5",
      title: "Write some code",
      description: "Do it everyday.",
      status: "todo",
      person: persons.OZ.name,
      color: persons.OZ.color
    },
    {
      id: "card6",
      title: "Work kanban project",
      description: "Last step",
      status: "todo",
      person: persons.LE.name,
      color: persons.LE.color
    },
    {
      id: "card7",
      title: "Build an app",
      description: "Build a React app.",
      status: "done",
      person: persons.ME.name,
      color: persons.ME.color
    },
    {
      id: "card8",
      title: "Write some code",
      description: "Do it everyday.",
      status: "done",
      person: persons.AM.name,
      color: persons.AM.color
    },
    {
      id: "card9",
      title: "Work kanban project",
      description: "Last step",
      status: "done",
      person: persons.OZ.name,
      color: persons.OZ.color
    }
  ]

},
reducers:{
  setListData:(state,action)=>{
    state.listData = action.payload
  },

}
})

export const { setListData} = taskSlice.actions;


export default taskSlice.reducer;
