import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface Task {
  description: string;
  id: string;
  title: string;
  person: string;
}

interface List {
  id: string;
  listName: string;
  status: string;
  task: Task[];
}

interface TaskState {
  listData: List[];
  openCardInputId: string | null;
  description: string;
  showDescription: boolean;
  title: string;
  searchQuery: string;
}

const initialState: TaskState = {
  listData: [
    // ... (your initial data here)
  ],
  openCardInputId: null,
  description: '',
  showDescription: false,
  title: '',
  searchQuery: '',
};

const taskSlice = createSlice({
  name: 'tasks',
  initialState,
  reducers: {
    setListData: (state, action: PayloadAction<List[]>) => {
      state.listData = action.payload;
    },
    setOpenCardInput: (state, action: PayloadAction<string | null>) => {
      state.openCardInputId = action.payload;
    },
    setDescription: (state, action: PayloadAction<string>) => {
      state.description = action.payload;
    },
    setShowDescription: (state, action: PayloadAction<boolean>) => {
      state.showDescription = action.payload;
    },
    setTitle: (state, action: PayloadAction<string>) => {
      state.title = action.payload;
    },
    setSearchQuery: (state, action: PayloadAction<string>) => {
      state.searchQuery = action.payload;
    },
  },
});

export const {
  setListData,
  setOpenCardInput,
  setDescription,
  setShowDescription,
  setTitle,
  setSearchQuery,
} = taskSlice.actions;

export default taskSlice.reducer;
