import React, { useEffect, useState } from "react";
import Task from "../task/Task";
import { setListData, setOpenCardInput, setSearchQuery } from "../../../store/slices/taskSlices";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { Droppable } from "react-beautiful-dnd";
import AddCardIcon from "@mui/icons-material/AddCard";
import Input from "../../atoms/input/Input";

const TaskContainer = styled.div`
  min-width: 350px;
  padding: 25px 25px 25px 15px;
`;
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
`;
const Title = styled.h2`
  display: flex;
  font-size: 18px;
  border-radius: 3px;
  padding: 16px;
  font-weight: bold;
  margin-bottom: 30px;
  color: #575064;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.1);
  border-top: 2px solid transparent;
  text-transform: uppercase;
  background: #fff;
  justify-content: space-between;
`;
const NumOfTask = styled.span`
  color: lightgray;
  display: inline-block;
  margin-left: 10px;
`;
const TaskController = () => {
  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);
  const openCardInputId = useSelector((state) => state.tasks.openCardInputId);
  const searchQuery = useSelector((state) => state.tasks.searchQuery);

  const handleSearchInputChange = (e) => {
    dispatch(setSearchQuery(e.target.value));
  };
  // Filter tasks based on the search query
  const filteredTasks = listData.reduce((acc, list) => {
    const filteredList = {
      ...list,
      task: list.task.filter((task) =>
          task.title.toLowerCase().includes(searchQuery.toLowerCase())
      ),
    };

    if (filteredList.task.length > 0) {
      acc.push(filteredList);
    }

    return acc;
  }, []);

  useEffect(() => {
    console.log("Updated openCardInputId:", openCardInputId);
  }, [openCardInputId]);

  const getColor = (id) => {
    if (id === "todo") {
      return '#dd9692';
    } else if (id === "inProgress") {
      return '#25b2fe';
    } else if (id === "done") {
      return '#8ac449';
    }

    return 'black';
  };

  const handleAddButton = (listId) => {
    dispatch(setOpenCardInput(listId));
    console.log(openCardInputId, listId)
  }

  useEffect(() => {
    const localStorageData = localStorage.getItem("List");
    if (localStorageData) {
      dispatch(setListData(JSON.parse(localStorageData)));
    }
  }, []);

  return (
      <>
        <Input
            type="text"
            value={searchQuery}
            onChange={handleSearchInputChange}
            placeholder="Search tasks..."
        />
        <Wrapper>
          {filteredTasks.map((ele, index) => (
              <Droppable key={ele.id} index={index} droppableId={ele.id}>
                {(provided) => (
                    <TaskContainer
                        key={ele.id}
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                    >
                      <Title style={{borderColor: getColor(ele.id)}}>
                        <div>
                          {ele.listName}
                          <NumOfTask>({ele.task.length})</NumOfTask>
                        </div>
                        <AddCardIcon onClick={() => handleAddButton(ele.id)} style={{ color: getColor(ele.id), cursor: "pointer"}}/>
                      </Title>
                      <Task
                          id={ele.id}
                          color={getColor(ele.id)}
                          openCardInput={ele.id === openCardInputId}
                          Lname={ele.listName}
                          task={ele.task}
                      />
                    </TaskContainer>
                )}
              </Droppable>
          ))}
        </Wrapper>
      </>
  );
};

export default TaskController;
