import styled from 'styled-components';

const ButtonStyled = styled.button`
  display: inline-block;
  border-radius: 3px;
  padding: 0.5rem 0;
  width: 11rem;
  background: ${({ theme }) => theme.switchButton};
  color: ${({ theme }) => theme.switchButtonText};
  border: none;
  outline: none;
  &:active {
    zoom: 0.99;
    opacity: 0.9;
  }
`;


export default function Button(props){
    return (
            <ButtonStyled onClick={props.onClick} style={props.style} type={props.type}> {props.text}</ButtonStyled>
    )
}
