import React, { useState } from "react";
import EditTwoToneIcon from "@mui/icons-material/EditTwoTone";
import DeleteIcon from "@mui/icons-material/Delete";
import { setListData } from '../../../store/slices/taskSlices'
import { useDispatch, useSelector } from 'react-redux'

const ListEdit = ({ title, id, cardId, isModalOpen, setIsModalOpen }) => {
  const [isInput, setIsInput] = useState(false);
  const [newTitle, setNewTitle] = useState(title);

  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData)

  const handleDelete = () => {
    const updatedList = listData.map(list => {
      if (list.id === cardId) {
        return {
          ...list,
          task: list.task.filter(task => task.id !== id)
        };
      }
      return list;
    });

    dispatch(setListData(updatedList));
    localStorage.setItem("List", JSON.stringify(updatedList));
  };

  const handleEdit = (e) => {
    e.preventDefault();
    const updatedList = listData.map(list => {
      if (list.id === cardId) {
        return {
          ...list,
          task: list.task.map(task => (task.id === id ? { ...task, title: newTitle } : task))
        };
      }
      return list;
    });

    dispatch(setListData(updatedList));
    localStorage.setItem("List", JSON.stringify(updatedList));
    setIsInput(!isInput);
  };

  const handleNavigate = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
      <div>
        <div>
          {isInput ? (
              <form onSubmit={handleEdit}>
                <input
                    type="text"
                    defaultValue={title}
                    onChange={(e) => setNewTitle(e.target.value)}
                />
              </form>
          ) : (
              <p onClick={handleNavigate}>{newTitle}</p>
          )}
        </div>
        <span>
        <EditTwoToneIcon onClick={() => setIsInput(!isInput)} />
        <DeleteIcon onClick={handleDelete} />
      </span>
      </div>
  );
};

export default ListEdit;
