import React, { useEffect } from "react";
import Card from "../card/card";
import { setListData } from "../../../store/slices/taskSlices";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { Droppable } from "react-beautiful-dnd";

const Title = styled.h2`
  font-size: 24px;
  font-family: "Open Sans", sans-serif;
  font-weight: bold;
  color: ${({ theme }) => theme.listTitle};
  margin-bottom: 30px;
`;

const ListController = () => {
  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);

  const getColor = (id) => {
    if (id === "todo") {
      return '#a991d1'; // Purple color for todo
    } else if (id === "inProgress") {
      return '#dd9692'; // Grey color for inProgress
    } else if (id === "done") {
      return 'green'; // Green color for done
    }
    // Default color if none of the conditions match
    return 'black';
  };

  useEffect(() => {
    const localStorageData = localStorage.getItem("List");
    if (localStorageData) {
      dispatch(setListData(JSON.parse(localStorageData)));
    }
  }, []);
console.log(listData)

  return (
    <div style={{ display: "flex" }}>
      {listData.map((ele, index) => (
        <Droppable key={ele.id} index={index} droppableId={ele.id}>
          {(provided) => (
            <div
              className={Styles.mainCard}
              key={ele.id}
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <Title style={{backgroundColor: getColor(ele.id)}}>
                {ele.listName}
              </Title>
              <Card id={ele.id} color={getColor(ele.id)} Lname={ele.listName} task={ele.task} />
            </div>
          )}
        </Droppable>
      ))}
    </div>
  );
};

export default ListController;
