import React, { useState, useRef, useEffect } from "react";
import TextField from "@mui/material/TextField";
import { setListData } from "../../../store/slices/taskSlices";
import CloseIcon from "@mui/icons-material/Close";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import AddIcon from "@mui/icons-material/Add";
import ListEdit from '../../organism/listEditable/listEditable'
import AddCardIcon from '@mui/icons-material/AddCard';
import uuid from "react-uuid";
import { useDispatch, useSelector } from "react-redux";
import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";
import { addCard } from "../../../store/actions/addCard";
import { setOpenCardInputId } from "../../../store/slices/taskSlices";


const KanbanCardContainer = styled.div`
  height: 158px;
  margin-bottom: 20px;
  padding: 16px;
  background-color: #fff;
  box-sizing: border-box;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.23);
  border-radius: 5px;
  border-left: 10px solid transparent;
`
const Title = styled.h4`
  margin-top: 0;
  margin-bottom: 15px;
  font-family: "Open Sans", sans-serif;
  font-size: 16px;
  font-weight: 600;
  color: #323232;
`;

const Content = styled.p`
  font-family: "Open Sans", sans-serif;
  font-weight: 400;
  font-size: 14px;
  color: darkgrey;
  margin-bottom: 13px;
`;

const Card = ({id, openCardInput, task, color}) => {
  const [title, setTitle] = useState("");
  // const [addItem, setAddItem] = useState(false);

  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);
  const openCardInputId = useSelector((state) => state.tasks.openCardInputId);


  const handleSubmit = () => {
    addCard(title, id, listData, dispatch);
    setOpenCardInputId(null)
    setTitle("");
  }

  function handleChange(e) {
    setTitle(e.target.value);
  }

  // function handleAddButton() {
  //   console.log(addItem)
  //   setAddItem(!addItem);
  // }

  return (
    <div>
      <div>
        {task && task.length > 0
          ? task.map((ele, index) => (
            <Draggable key={ele.id} draggableId={ele.id} index={index}>
              {(provided) => (
                <div
                  key={ele.id}
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                >
                  <KanbanCardContainer style={{borderColor: color}}>
                  {/*<Title>{ele.title}</Title>*/}
                    <ListEdit title={ele.title} id={ele.id} cardId={id} />
                    <Content>{ele.description}</Content>
                    <Content>{ele.comment}</Content>
                  </KanbanCardContainer>
                 </div>
              )}
            </Draggable>

          ))
          : null}
      </div>
      {!openCardInput ? (
        <div>
          <div>
            {/*<button onClick={handleAddButton}>*/}
            {/*  <AddIcon*/}
            {/*    sx={{ marginBottom: "-5px", paddingRight: "4px" }}*/}
            {/*    fontSize="small"*/}
            {/*    color="#B7BCC7"*/}
            {/*  />*/}
            {/*  Add a card*/}
            {/*</button>*/}
          </div>
          <div>
            <AddCardIcon fontSize="small" color="blue" />
          </div>
        </div>
      ) : (
        <div>
          <div>
            <TextField
              id="outlined-multiline-static"
              placeholder="Enter card name"
              multiline
              value={title}
              onChange={handleChange}
              rows={2}
              sx={{ width: "100%" }}
            />
          </div>
          <div>
            <div>
              <button onClick={handleSubmit}>Add Card</button>
              {/*<CloseIcon onClick={handleAddButton} />*/}
            </div>
            <div style={{ cursor: "pointer" }}>
              <MoreHorizIcon fontSize="large" />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Card;
