import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import EditNoteIcon from "@mui/icons-material/EditNote";
import CloseIcon from "@mui/icons-material/Close";
import { useNavigate, useParams } from "react-router";
import { setListData, setDescription, setShowDescription } from "../../../store/slices/taskSlices";
import { useDispatch, useSelector } from 'react-redux';
import DashboardIcon from '@mui/icons-material/Dashboard';
import styled from "styled-components"
import Button  from "../../atoms/button/Button"

const Title = styled.h4`
  font-size: 16px;
  font-weight: 600;
  color: #323232;
`;
const Paragraph = styled.p`
  font-size: 14px;
  color: lightgray;
  margin-bottom: auto;
  margin-top:15px;
`;
const Description = styled.h4`
  font-size: 14px;
  color: dimgray;
  margin: 20px 0;
`;
const TitleHolder = styled.div`
  margin-top: 10px;
  display: flex;
  align-items: center;
  text-transform: capitalize;
`;
const Person = styled.span`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 28px;
  width: 28px;
  border-radius: 100%;
  font-size: 10px;
  font-family: "Lato", sans-serif;
  font-weight: 900;
  color: #ffffff;
  background-color: #c335fb;
  margin-left: auto;
`;
const InputSelectPerson = styled.select`
  display: block;
  width: 100%;
  margin-bottom: 10px;
`;

export default function TaskInfo({Cid, Lid}) {
  const [currentCardTitle, setCurrentCardTitle] = useState("");
  const [currentTaskTitle, setCurrentTaskTitle] = useState("");
  const [currentTask, setCurrentTask] = useState({});


  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);
  const description = useSelector((state) => state.tasks.description);
  const showDescription = useSelector((state) => state.tasks.showDescription);

  useEffect(() => {
    let input = [...listData];
    let index = input.findIndex((ele) => ele.id === Lid);

    let currentCard = { ...input[index] };
    setCurrentCardTitle(currentCard.listName);

    let taskss = { ...currentCard };
    let Task = [...taskss.task];

    let taskindex = Task.findIndex((ele) => ele.id === Cid);

    let currentTask = Task[taskindex];

    if (currentTask) {
      setDescription(currentTask.description);
      setCurrentTaskTitle(currentTask.title);
      setCurrentTask(currentTask);
    }
  }, [showDescription, listData, Lid, Cid]);

  function handleDescription() {
    const updatedListData = listData.map((ele) => {
      if (ele.id === Lid) {
        return {
          ...ele,
          task: ele.task.map((task) =>
              task.id === Cid ? { ...task, description } : task
          ),
        };
      }
      return ele;
    });



    dispatch(setListData(updatedListData));
    localStorage.setItem("List", JSON.stringify(updatedListData));
    dispatch(setShowDescription(false));
  }
  function handleDescription() {
    const updatedListData = listData.map((ele) => {
      if (ele.id === Lid) {
        return {
          ...ele,
          task: ele.task.map((task) =>
              task.id === Cid ? { ...task, description } : task
          ),
        };
      }
      return ele;
    });
    dispatch(setListData(updatedListData));
    localStorage.setItem("List", JSON.stringify(updatedListData));
    dispatch(setShowDescription(false));
  }
  return (
      <>
        <TitleHolder>
          <DashboardIcon />
            <Title>{currentTaskTitle}</Title>
        </TitleHolder>
        <Paragraph>in List {currentCardTitle}</Paragraph>
        <Title>Assigned To:
          <InputSelectPerson
              value={(e) => e.current.target.value}
              onChange={handleDescription}
          >
            <option value="OZ">OZ</option>
            <option value="LE">LE</option>
            <option value="ME">ME</option>
            <option value="AM">AM</option>
          </InputSelectPerson>
          <Person>{currentTask.person}</Person>
        </Title>
        <TitleHolder><EditNoteIcon fontSize="large" /> <Title>Description</Title></TitleHolder>
        {!showDescription ? (
            <div>
              <Description>{currentTask.description}</Description>
              <Button text="Edit" onClick={() => dispatch(setShowDescription(true))} />
            </div>
        ) : (
            <>
              <TextField
                  multiline
                  sx={{ width: "100%" }}
                  defaultValue={currentTask.description}
                  onChange={(e) => {
                    dispatch(setDescription(e.target.value));
                  }}
              />
              <Button
                  text="Save"
                  onClick={handleDescription}
                  type="add"
              >
                Save
              </Button>
              <Button
                  text="Cancel"
                  onClick={() => {
                    dispatch(setShowDescription(false));
                    dispatch(setDescription(currentTask.description));
                  }}
                  type="cancel"
              />
            </>
        )}
      </>
  );
}
