import React, { useEffect } from "react";
import Card from "../card/card";
import { setListData } from "../../../store/slices/taskSlices";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { Droppable } from "react-beautiful-dnd";
import AddIcon from "@mui/icons-material/Add";
import AddCardIcon from "@mui/icons-material/AddCard";
import TextField from "@mui/material/TextField";
import CloseIcon from "@mui/icons-material/Close";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import {useState} from "@types/react";
import uuid from "react-uuid";

const TaskContainer = styled.div`
  min-width: 320px;
  padding: 25px 25px 25px 15px;
`;
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
`;
const Title = styled.h2`
  font-size: 18px;
  border-radius: 3px;
  padding: 16px;
  font-weight: bold;
  margin-bottom: 30px;
  color: #575064;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.1);
  border-top: 2px solid transparent;
  text-transform: uppercase;
  background: #fff;
`;

const ListController = () => {
  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);

  const getColor = (id) => {
    if (id === "todo") {
      return '#dd9692';
    } else if (id === "inProgress") {
      return '#25b2fe';
    } else if (id === "done") {
      return '#8ac449';
    }
    // Default color if none of the conditions match
    return 'black';
  };
  const [title, setTitle] = useState("");
  const [addItem, setAddItem] = useState(false);


  function handleSubmit() {
    let TitleOfList = title.trim();
    if (TitleOfList !== "") {
      let input = [...listData];

      let newTask = {
        id: uuid(),
        title: title,
        description: "",
        comments: [],
      };

      const temporary = {
        id: id,
        Lname: Lname,
        task: [...task, newTask],
      };

      let index = listData.findIndex((ele) => ele.id === id);
      input[index] = temporary;

      dispatch(setListData(input));

      localStorage.setItem("List", JSON.stringify(input));

      console.log(listData);
      setTitle("");
    }
  }

  function handleChange(e) {
    setTitle(e.target.value);
  }

  function handleAddButton() {
    console.log(addItem)
    setAddItem(!addItem);
  }

  useEffect(() => {
    const localStorageData = localStorage.getItem("List");
    if (localStorageData) {
      dispatch(setListData(JSON.parse(localStorageData)));
    }
  }, []);
console.log(listData)

  return (
    <Wrapper>
      {listData.map((ele, index) => (
        <Droppable key={ele.id} index={index} droppableId={ele.id}>
          {(provided) => (
            <TaskContainer
              key={ele.id}
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <Title style={{borderColor: getColor(ele.id)}}>
                {ele.listName}
                {!addItem ? (
                    <div>
                      <div>
                        <button onClick={handleAddButton}>
                          <AddIcon
                              sx={{ marginBottom: "-5px", paddingRight: "4px" }}
                              fontSize="small"
                              color="#B7BCC7"
                          />
                          Add a card
                        </button>
                      </div>
                      <div>
                        <AddCardIcon fontSize="small" color="blue" />
                      </div>
                    </div>
                ) : (
                    <div>
                      <div>
                        <TextField
                            id="outlined-multiline-static"
                            placeholder="Enter card name"
                            multiline
                            value={title}
                            onChange={handleChange}
                            rows={2}
                            sx={{ width: "100%" }}
                        />
                      </div>
                      <div>
                        <div>
                          <button onClick={handleSubmit}>Add Card</button>
                          <CloseIcon onClick={handleAddButton} />
                        </div>
                        <div style={{ cursor: "pointer" }}>
                          <MoreHorizIcon fontSize="large" />
                        </div>
                      </div>
                    </div>
                )}
              </Title>
              <Card id={ele.id} color={getColor(ele.id)} Lname={ele.listName} task={ele.task} />
            </TaskContainer>
          )}
        </Droppable>
      ))}
    </Wrapper>
  );
};

export default ListController;
