import TaskController from '../../organism/taskController/TaskController';
import { DragDropContext } from 'react-beautiful-dnd';
import styled from "styled-components";
import { handleDragEnd } from '../../../store/actions/index';
import {useDispatch,useSelector} from 'react-redux'
import { setSearchQuery } from "../../../store/slices/taskSlices";

const BoardContainer = styled.div`
  background: #f7f8f9;
  height: 100vh;
`;


const Board = () => {
    const dispatch = useDispatch();
    const listData = useSelector((state) => state.tasks.listData);
    const searchQuery = useSelector((state) => state.tasks.searchQuery);

    const handleSearchInputChange = (e) => {
        dispatch(setSearchQuery(e.target.value));
    };


    const onDragEnd = (result) => {
        handleDragEnd(result, dispatch, listData);
    };

    return (
        <BoardContainer>
            <DragDropContext onDragEnd={(result)=> onDragEnd(result)}>
                <input
                    type="text"
                    value={searchQuery}
                    onChange={handleSearchInputChange}
                    placeholder="Search tasks..."
                />
                <TaskController searchQuery={searchQuery} />
            </DragDropContext>
        </BoardContainer>
    )
}

export default Board;
