import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import AddCardIcon from "@mui/icons-material/AddCard";
import { useDispatch, useSelector } from "react-redux";
import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";
import { addCard } from "../../../store/actions/addCard";

const KanbanCardContainer = styled.div`
  height: 158px;
  margin-bottom: 20px;
  padding: 16px;
  background-color: #fff;
  box-sizing: border-box;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.23);
  border-radius: 5px;
  border-left: 10px solid transparent;
`;
const Title = styled.h4`
  margin-top: 0;
  margin-bottom: 15px;
  font-family: "Open Sans", sans-serif;
  font-size: 16px;
  font-weight: 600;
  color: #323232;
`;

const Content = styled.p`
  font-family: "Open Sans", sans-serif;
  font-weight: 400;
  font-size: 14px;
  color: darkgrey;
  margin-bottom: 13px;
`;

const Card = ({ id, task, color }) => {
  const [title, setTitle] = useState("");
  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);

  useEffect(() => {
    // Reset title when task changes (e.g., after adding a new card)
    setTitle("");
  }, [task]);

  const handleSubmit = () => {
    dispatch(addCard(id, title, listData));
    // No need to reset title here, as it is handled in useEffect
  };

  function handleChange(e) {
    setTitle(e.target.value);
  }

  return (
      <div>
        <div>
          {task && task.length > 0 ? (
              task.map((ele, index) => (
                  <Draggable key={ele.id} draggableId={ele.id} index={index}>
                    {(provided) => (
                        <div
                            key={ele.id}
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                        >
                          <KanbanCardContainer style={{ borderColor: color }}>
                            <Title>{ele.title}</Title>
                            <Content>{ele.description}</Content>
                            <Content>{ele.comment}</Content>
                          </KanbanCardContainer>
                        </div>
                    )}
                  </Draggable>
              ))
          ) : null}
        </div>
        {!task || task.length === 0 ? (
            <div>
              <div>
                <AddCardIcon fontSize="small" color="blue" />
              </div>
            </div>
        ) : (
            <div>
              <div>
                <TextField
                    id="outlined-multiline-static"
                    placeholder="Enter card name"
                    multiline
                    value={title}
                    onChange={handleChange}
                    rows={2}
                    sx={{ width: "100%" }}
                />
              </div>
              <div>
                <div>
                  <button onClick={handleSubmit}>Add Card</button>
                </div>
                <div style={{ cursor: "pointer" }}>
                  <MoreHorizIcon fontSize="large" />
                </div>
              </div>
            </div>
        )}
      </div>
  );
};

export default Card;
