import ListController from '../../organism/ListController/listController';
import Navbar from '../../organism/navbar/navbar'
import { DragDropContext } from 'react-beautiful-dnd';
import styled from "styled-components";
import { handleDragEnd } from '../../../store/actions/index';
import {useDispatch,useSelector} from 'react-redux'

const BoardContainer = styled.div`
  display: flex;
  flex-flow: row wrap;
  justify-content: space-between;
  /* padding-top: 34px; */
  padding-left: 44px;
  background-image: url(https://img.freepik.com/premium-photo/toned-pink-purple-blue-teal-shiny-surface-background-illustration_739548-3018.jpg?w=740);
height: 100vh;
  padding-right: 44px;
`;


const Board = () => {
    const dispatch = useDispatch();
    const listData = useSelector((state) => state.tasks.listData);

    const onDragEnd = (result) => {
        handleDragEnd(result, dispatch, listData);
    };

    return (
        <BoardContainer>

            <Navbar />

            <DragDropContext onDragEnd={(result)=> onDragEnd(result)}>
                <div className='board-wrapper'>
                    <ListController />
                </div>
            </DragDropContext>
        </BoardContainer>
    )
}

export default Board;
