import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import ListEdit from '../listEdit/ListEdit'
import Modal from "./../../molecules/Modal";
import { useDispatch, useSelector } from "react-redux";
import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";
import { addCard } from "../../../store/actions/addCard";
import { setOpenCardInput } from "../../../store/slices/taskSlices";
import Button from "../../atoms/button/Button";
import KenbanCard from "../../molecules/KenbanCard";

const TaskContainer = styled.div`
  cursor: pointer;
`;
const KanbanCardContainer = styled.div`
  min-height: 158px;
  margin-bottom: 20px;
  padding: 16px;
  background-color: #fff;
  box-sizing: border-box;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.23);
  border-radius: 5px;
  border-left: 10px solid transparent;
`;

const Content = styled.p`
  font-family: "Open Sans", sans-serif;
  font-weight: 400;
  font-size: 14px;
  color: darkgrey;
  margin-bottom: 13px;
`;
const ButtonHolder = styled.div`
  display: flex;
  margin-top: 15px;
  justify-content: flex-end;
`;
const Person = styled.span`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 28px;
  width: 28px;
  border-radius: 100%;
  font-size: 10px;
  font-family: "Lato", sans-serif;
  font-weight: 900;
  color: #ffffff;
  background-color: #c335fb;
`;

const Task = ({id, task, color, searchQuery}) => {
  const [title, setTitle] = useState("");
  const [taskModals, setTaskModals] = useState({});
  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);
  const openCardInputId = useSelector((state) => state.tasks.openCardInputId);
  const filteredTasks = task.filter((item) =>
      item.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleToggleModal = (taskId) => {
    setTaskModals((prevModals) => ({ ...prevModals, [taskId]: !prevModals[taskId] }));
  };

  const handleSubmit = () => {
    addCard(title, id, listData, dispatch);
    dispatch(setOpenCardInput(null));
    setTitle("");
  }

  function handleChange(e) {
    setTitle(e.target.value);
  }
  const handleCancel = () => {
    dispatch(setOpenCardInput(null));
    setTitle("");
  }
  const handleDoubleClick = (taskId) => {
    if (!openCardInputId) {
      handleToggleModal(taskId);
    }
  };

  return (
      <div>
        {filteredTasks && filteredTasks.length > 0
            ? filteredTasks.map((item, index) => (
                <React.Fragment key={item.id}>
                 {/*<KenbanCard*/}
                 {/*    index={index}*/}
                 {/*    item={item}*/}
                 {/*    id={item.id}*/}
                 {/*    color={color}*/}
                 {/*    handleToggleModal={handleToggleModal}*/}
                 {/*    handleDoubleClick={handleDoubleClick}*/}
                 {/*    taskModals={taskModals}*/}
                 {/*/>*/}
                 {/* <Modal*/}
                 {/*     isModalOpen={taskModals[item.id]}*/}
                 {/*     setIsModalOpen={() => handleToggleModal(item.id)}*/}
                 {/*     task={item}*/}
                 {/*     id={id}*/}
                 {/* />*/}
                </React.Fragment>
            ))
            : null}
        {(openCardInputId && openCardInputId === id) && (
            <div>
              <div>
                <TextField
                    id="outlined-multiline-static"
                    placeholder="Enter card name"
                    multiline
                    value={title}
                    onChange={handleChange}
                    rows={2}
                    sx={{ width: "100%" }}
                />
              </div>
              <ButtonHolder>
                <Button text="Add ticket" onClick={handleSubmit} type="add" />
                <Button text="Cancel" onClick={handleCancel} type="cancel" />
              </ButtonHolder>
            </div>
        )}
      </div>
  );
};

export default Task;
