import React, { useEffect } from "react";
import Card from "../card/card";
import { setListData } from "../../../store/slices/taskSlices";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { Droppable } from "react-beautiful-dnd";
import { addCard } from "../../../store/actions/addCard";

const TaskContainer = styled.div`
  min-width: 320px;
  padding: 25px 25px 25px 15px;
`;
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
`;
const Title = styled.h2`
  font-size: 18px;
  border-radius: 3px;
  padding: 16px;
  font-weight: bold;
  margin-bottom: 30px;
  color: #575064;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.1);
  border-top: 2px solid transparent;
  text-transform: uppercase;
  background: #fff;
`;

const ListController = () => {
  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);

  const getColor = (id) => {
    if (id === "todo") {
      return '#dd9692';
    } else if (id === "inProgress") {
      return '#25b2fe';
    } else if (id === "done") {
      return '#8ac449';
    }
    // Default color if none of the conditions match
    return 'black';
  };
  const handleSubmit = () => {
    addCard(title, id, listData, dispatch);
    setTitle("");
  }
  useEffect(() => {
    const localStorageData = localStorage.getItem("List");
    if (localStorageData) {
      dispatch(setListData(JSON.parse(localStorageData)));
    }
  }, []);

  return (
    <Wrapper>
      {listData.map((ele, index) => (
        <Droppable key={ele.id} index={index} droppableId={ele.id}>
          {(provided) => (
            <TaskContainer
              key={ele.id}
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <Title style={{borderColor: getColor(ele.id)}}>
                {ele.listName}
              </Title>
              <Card id={ele.id} color={getColor(ele.id)} Lname={ele.listName} task={ele.task} />
            </TaskContainer>
          )}
        </Droppable>
      ))}
    </Wrapper>
  );
};

export default ListController;
