import { createSlice } from '@reduxjs/toolkit';

const taskSlice = createSlice({
name:'tasks',
initialState:{
  listData:[
    {
      id: "todo",
      listName: "To Do",
      status: "backlog",
      task: [
        {
          description: "task1",
          id: "task1",
          title: "Work kanban project",
          person: "Joe",
        },
        {
          description: "test",
          id: "task2",
          title: "Write some code",
          person: "Ann",
        },
        {
          description: "test",
          id: "task3",
          title: "Build an app",
          person: "Joe",
        },
      ]
    },
    {
      id: "inProgress",
      listName: "In Progress",
      status: "progress",
      task: [
        {
          description: "task1",
          id: "task4",
          title: "Work kanban project",
          person: "Joe",
        },
        {
          description: "test",
          id: "task5",
          title: "Write some code",
          person: "Joe",
        },
      ]
    },
    {
      id: "done",
      listName: "Done",
      status: "done",
      task: [
        {
          description: "task1",
          id: "task6",
          title: "Work kanban project",
          person: "Joe",
        },
        {
          description: "test",
          id: "task7",
          title: "Write some code",
          person: "Joe",
        },
      ]
    },
  ],
  openCardInputId: null,
  description: '',
  showDescription: false,
  title: '',
},

reducers:{
  setListData:(state,action)=>{
    state.listData = action.payload
  },
  setOpenCardInput: (state, action) => {
    state.openCardInputId = action.payload;
  },
  setDescription: (state, action) => {
    state.description = action.payload;
  },
  setShowDescription: (state, action) => {
    state.showDescription = action.payload;
  },
  setTitle: (state, action) => {
    state.title = action.payload;
  },
  setSelectedPerson: (state, action) => {
    state.selectedPerson = action.payload;
  },
}
})

export const { setListData, setOpenCardInput, setDescription, setShowDescription, setTitle, setSelectedPerson} = taskSlice.actions;


export default taskSlice.reducer;
