import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import ListEdit from '../listEdit/ListEdit'
import Modal from "./../../molecules/Modal";
import { useDispatch, useSelector } from "react-redux";
import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";
import { addCard } from "../../../store/actions/addCard";
import { setOpenCardInput } from "../../../store/slices/taskSlices";
import Button from "../../atoms/button/Button";

const KanbanCardContainer = styled.div`
  height: 158px;
  margin-bottom: 20px;
  padding: 16px;
  background-color: #fff;
  box-sizing: border-box;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.23);
  border-radius: 5px;
  border-left: 10px solid transparent;
`;

const Content = styled.p`
  font-family: "Open Sans", sans-serif;
  font-weight: 400;
  font-size: 14px;
  color: darkgrey;
  margin-bottom: 13px;
`;
const ButtonHolder = styled.div`
  display: flex;
  margin-top: 15px;
  justify-content: flex-end;
`;


const Task = ({id, task, color}) => {
  const [title, setTitle] = useState("");
  const [taskModals, setTaskModals] = useState({});
  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);
  const openCardInputId = useSelector((state) => state.tasks.openCardInputId);

  // // Initialize taskModals when listData changes
  // useEffect(() => {
  //   let initialModals = {};
  //   listData.forEach((list) => {
  //     list.task.forEach((task) => {
  //       initialModals[task.id] = false;
  //     });
  //   });
  //   setTaskModals(initialModals);
  // }, [listData]);

  // Function to handle opening/closing modal for a specific task
  const handleToggleModal = (taskId) => {
    setTaskModals((prevModals) => ({ ...prevModals, [taskId]: !prevModals[taskId] }));
  };

  const handleSubmit = () => {
    addCard(title, id, listData, dispatch);
    dispatch(setOpenCardInput(null));
    setTitle("");
  }

  function handleChange(e) {
    setTitle(e.target.value);
  }
  const handleCancel = () => {
    dispatch(setOpenCardInput(null));
    setTitle("");
  }

  return (
    <div><div>
      <div>
        {task && task.length > 0
            ? task.map((item, index) => (
                <React.Fragment key={item.id}>
                  <Draggable key={item.id} draggableId={item.id} index={index}>
                    {(provided) => (
                        <div
                            key={item.id}
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                        >
                          <KanbanCardContainer style={{ borderColor: color }}>
                            <ListEdit
                                title={item.title}
                                id={item.id}
                                cardId={id}
                                isModalOpen={taskModals[item.id]}
                                setIsModalOpen={() => handleToggleModal(item.id)}
                            />

                            <Content>{item.description}</Content>
                          </KanbanCardContainer>
                        </div>
                    )}
                  </Draggable>
                  <Modal
                      isModalOpen={taskModals[item.id]}
                      setIsModalOpen={() => handleToggleModal(item.id)}
                      task={item}
                      id={id}
                  />
                </React.Fragment>
            ))
            : null}
      </div>
    </div>
      {(openCardInputId && openCardInputId === id) && (
          <div>
            <div>
              <TextField
                  id="outlined-multiline-static"
                  placeholder="Enter card name"
                  multiline
                  value={title}
                  onChange={handleChange}
                  rows={2}
                  sx={{ width: "100%" }}
              />
            </div>
            <ButtonHolder>
              <Button text="Add ticket" onClick={handleSubmit} type="add" />
              <Button text="Cancel" onClick={handleCancel} type="cancel" />
            </ButtonHolder>
          </div>
      )}
    </div>
  );
};

export default Task;
