import React, { useEffect, useState } from "react";
import Card from "../card/card";
import { setListData, setOpenCardInput } from "../../../store/slices/taskSlices";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { Droppable } from "react-beautiful-dnd";
import AddCardIcon from "@mui/icons-material/AddCard";

const TaskContainer = styled.div`
  min-width: 320px;
  padding: 25px 25px 25px 15px;
`;
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
`;
const Title = styled.h2`
  display: flex;
  font-size: 18px;
  border-radius: 3px;
  padding: 16px;
  font-weight: bold;
  margin-bottom: 30px;
  color: #575064;
  box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.1);
  border-top: 2px solid transparent;
  text-transform: uppercase;
  background: #fff;
  justify-content: space-between;
`;

const ListController = () => {
  const [addItem, setAddItem] = useState(false);
  // const [openCardInputId, setOpenCardInputId] = useState(null);

  const dispatch = useDispatch();
  const listData = useSelector((state) => state.tasks.listData);
  const openCardInputId = useSelector((state) => state.tasks.openCardInputId);
  useEffect(() => {
    console.log("Updated openCardInputId:", openCardInputId);
  }, [openCardInputId]);
  const getColor = (id) => {
    if (id === "todo") {
      return '#dd9692';
    } else if (id === "inProgress") {
      return '#25b2fe';
    } else if (id === "done") {
      return '#8ac449';
    }
    // Default color if none of the conditions match
    return 'black';
  };
  // const handleSubmit = (id) => {
  //   addCard(title, id, listData, dispatch);
  //   setTitle("");
  // }
  function handleAddButton(listId) {
    dispatch(setOpenCardInput(listId));
    console.log(openCardInputId, listId)
  }

  useEffect(() => {
    const localStorageData = localStorage.getItem("List");
    if (localStorageData) {
      dispatch(setListData(JSON.parse(localStorageData)));
    }
  }, []);

  return (
    <Wrapper>
      {listData.map((ele, index) => (
        <Droppable index={index} droppableId={ele.id}>
          {(provided) => (
            <TaskContainer
              key={ele.id}
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <Title style={{borderColor: getColor(ele.id)}}>
                {ele.listName}
                {/*<button  onClick={() => handleAddButton(ele.id)}>*/}
                {/*  <AddIcon*/}
                {/*      sx={{ marginBottom: "-5px", paddingRight: "4px" }}*/}
                {/*      fontSize="small"*/}
                {/*      color="#B7BCC7"*/}
                {/*  />*/}
                {/*  Add a card*/}
                {/*</button>*/}
                <AddCardIcon  onClick={() => handleAddButton(ele.id)} fontSize="small" color="#fff" style={{ color: getColor(ele.id)}}/>
              </Title>
              <Card
                  id={ele.id}
                  color={getColor(ele.id)}
                  openCardInput={ele.id === openCardInputId}
                  Lname={ele.listName}
                  task={ele.task}
                  addItem={addItem}
              />
            </TaskContainer>
          )}
        </Droppable>
      ))}
    </Wrapper>
  );
};

export default ListController;
