import React from "react";
import styled from "styled-components";
import CardInfo from "./../organism/cardinfo/cardInfo";
import CloseIcon from "@mui/icons-material/CloseIcon";
import Button  from "../../atoms/button/Button"

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  background: #fff;
  padding: 20px;
  border-radius: 8px;
`;

const Modal = ({ isModalOpen, setIsModalOpen, task, id }) => {
    if (!isModalOpen) return null;
    return (
        <ModalOverlay>
            <ModalContent>
                <CardInfo Cid={task.id} Lid={id} />
                <Button onClick={() => setIsModalOpen(false)}><CloseIcon /></Button>
            </ModalContent>
        </ModalOverlay>
    );
};

export default Modal;
