import TaskController from '../../organism/taskController/TaskController';
import { DragDropContext } from 'react-beautiful-dnd';
import styled from "styled-components";
import { handleDragEnd } from '../../../store/actions/index';
import {useDispatch,useSelector} from 'react-redux';

const BoardContainer = styled.div`
  background: #f7f8f9;
  height: 100vh;
  padding-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center
`;


const Board = () => {
    const dispatch = useDispatch();
    const listData = useSelector((state) => state.tasks.listData);

    const onDragEnd = (result) => {
        handleDragEnd(result, dispatch, listData);
    };

    return (
        <BoardContainer>
            <DragDropContext onDragEnd={(result)=> onDragEnd(result)}>
                <TaskController />
            </DragDropContext>
        </BoardContainer>
    )
}

export default Board;
